<?php
	// Removes empty <p> tags generated by shortcodes...
    add_filter('the_content', 'shortcode_empty_paragraph_fix');
    function shortcode_empty_paragraph_fix($content){   
        $array = array ('<p>[' => '[', ']</p>' => ']', ']<br />' => ']');
        return strtr($content, $array);
    }
?>